        .a16
@bfe9:  tay
        plx
        lda     #$0010
        sta     $14
@bff0:  lda     $0000,y     ; copy to condemned numeral
        sta     a:$0000,x
        inx2
        iny2
        dec     $14
        bne     @bff0
        rts
        .a8

