.macro def_monster_sprite_map x_pos, y_pos, width, height
        ; position and size converted to 32x32 sprites
        .local  x32, y32, height32, width32
        x32 = x_pos / 4
        y32 = y_pos / 4
        width32 = width / 4
        height32 = height / 4
        ; loop over 32x32 sprites
        .byte   x_pos*8, y_pos*8
        .repeat height32, _y
        .repeat width32, _x
        .byte   x32+_x+(y32+_y)*4
        .endrep
        .endrep
        ; terminator
        .byte   $ff
.endmac

