; [ execute misc. monster animation (ai command $fa, command $2b) ]

DoMonsterAnim:
@e63d:  ldy     #$0002
        lda     ($76),y
        sta     $e9fc       ; target monster
        dey
        lda     ($76),y     ; ai command $fa subcommand
        cmp     #$09
        bne     @e659       ; branch if not $09 (play sound effect)
        iny
        lda     ($76),y     ; sound effect
        sta     $10
        iny
        lda     ($76),y
        jsl     PlayAnimSfx_far
        rtl
@e659:  longa
        asl
        tax
        lda     $c2e5d3,x   ; pointer to misc. monster animation script
        sta     $8f
        shorta0
        bra     _e6b1

; ------------------------------------------------------------------------------

; [ execute monster entry/exit animation ]

DoMonsterEntryExit:
@e668:  jsr     WaitLine160_near
        ldy     #$0001
        lda     ($76),y     ; entry/exit type
        cmp     #$0d
        bne     @e67a       ; branch if not $0d (flash in/out)
        jsr     _c2e806
        jmp     _c2e88e
@e67a:  cmp     #$11
        bne     @e691       ; branch if not $11 (final kefka death)

; final kefka death
        jsr     _c2e806
        jsl     KefkaDeathAnim_far
        lda     #$ff
        sta     $6191
        lda     $2f2f
        sta     $201e
        rtl

; all other monster entry/exit animations
@e691:  pha
        jsr     _c2e806
        pla
        longa
        asl
        tax
        lda     $e9fb
        and     #$00ff
        beq     @e6a8       ; branch if monsters are entering
        lda     $c2e4eb,x   ; pointers to monster exit animation scripts
        bra     @e6ac
@e6a8:  lda     $c2e50f,x   ; pointers to monster entry animation scripts
@e6ac:  sta     $8f
        shorta0

