        sta     $91
@e6b5:  lda     [$8f]
        cmp     #$ff
        beq     @e6cb       ; branch if end of data
        cmp     #$fe
        beq     @e6d0       ;
        asl
        tax
        jsr     (.loword(MonsterAnimCmdTbl),x)
        ldy     $8f
        iny
        sty     $8f
        bra     @e6b5
@e6cb:  lda     #$ff
        sta     $6191
@e6d0:  lda     $2f2f
        sta     $201e
        rtl

