; ------------------------------------------------------------------------------

.segment "event_script"

begin_fixed_block EventScript, $02e600

; ------------------------------------------------------------------------------

; ca/0000
.proc NoEvent
        set_script_mode EVENT
        no_event
.endproc  ; NoEvent

; ------------------------------------------------------------------------------

; ca/0001
.proc WaitDlg
        wait_dlg
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0003
.proc GameStart_ext
        call GameStart
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0008
.proc TreasureItem
        dlg $0b85                       ; Received ``{item}''!
        return
.endproc

; ------------------------------------------------------------------------------

; ca/000c
.proc TreasureMagic
        dlg $0b86                       ; Learned ``{spell}''!
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0010
.proc TreasureGil
        dlg $0b87                       ; Found {gp} GP!
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0014
.proc TreasureEmpty
        dlg $0b88                       ; Empty!
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0018
.proc RandBattle
        obj_script SLOT_2, ASYNC
                scroll_obj
                end
        obj_script SLOT_3, ASYNC
                scroll_obj
                end
        obj_script SLOT_4, ASYNC
                scroll_obj
                end
        obj_script SLOT_1
                scroll_obj
                end
        rand_battle
        if_b_switch $40, :+

game_over:
        call GameOver
:       update_party
        return
.endproc  ; RandBattle

GameOver_ext := RandBattle::game_over

; ------------------------------------------------------------------------------

; ca/0034
.proc Tent_ext
        call Tent
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0039
.proc Warp_ext
        if_switch $0127=0, Warp
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0040
.proc TreasureMonster
        dlg $0b90                       ; Monster-in-a-box!
        call _cb0e1c
        treasure_battle
        call _ca5ea9
        update_party
        fade_in
        return
.endproc

; ------------------------------------------------------------------------------

; ca/004f
.proc WorldTent_ext
        set_script_mode WORLD
        fade_out
        wait 2
        load_map 3, {8, 8}, UP, {Z_UPPER, SET_PARENT}
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0059
.proc AirshipGround
        set_script_mode WORLD
        if_all
                switch $009d=1
                switch $009e=0
                goto _ca5ad4
        load_map 7, {8, 35}, UP, SET_PARENT
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0068
.proc AirshipDeck
        set_script_mode WORLD
        switch $0246=1

; blackjack
        if_switch $00a4=1, :+
        load_map 6, {16, 6}, DOWN, {SET_PARENT, STARTUP_EVENT}
        set_script_mode EVENT
        return

; falcon
:       set_script_mode WORLD
        load_map 11, {16, 8}, LEFT, {SET_PARENT, STARTUP_EVENT}
        set_script_mode EVENT
        return
.endproc  ; AirshipDeck

; ------------------------------------------------------------------------------

; ca/007f
.proc EnterKefkasTower_ext
        set_script_mode VEHICLE
        move_vehicle UP, 64
        load_map 17, {15, 8}, LEFT, {Z_UPPER, SET_PARENT}
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0088
.proc EnterPhoenixCave_ext
        set_script_mode WORLD
        load_map 17, {16, 8}, LEFT, {Z_UPPER, SET_PARENT}
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/008f
.proc EnterGogosLair
        set_script_mode WORLD
        load_map $0114, {10, 12}, UP, {Z_UPPER, SET_PARENT, STARTUP_EVENT}
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/0096
.proc DoomGazeDefeated
        set_script_mode WORLD
        load_map 17, {17, 8}, LEFT, {Z_UPPER, SET_PARENT, AIRSHIP}
        set_script_mode EVENT
        return
.endproc

; ------------------------------------------------------------------------------

; ca/009d
.proc DoomGazeMagicite
        create_obj NPC_3
        show_obj NPC_3
        sort_obj
        obj_script SLOT_1
                dir LEFT
                speed NORMAL
                end
        obj_script NPC_3
                speed FASTER
                move RIGHT_RIGHT_DOWN, 6
                speed NORMAL
                jump_high
                move RIGHT, 2
                speed SLOW
                jump_low
                move RIGHT, 1
                end
        obj_script SLOT_1
                dir RIGHT
                end
        wait_1s
        call _cac810, 3
        wait_45f
        dlg $0b9e                       ; A piece of Magicite popped out of
                                        ; Doom Gaze's mouth!
        obj_script SLOT_1
                move RIGHT, 2
                dir DOWN
                end
        pass_off SLOT_1
        pass_off NPC_3
        obj_script NPC_3
                speed SLOWER
                move UP, 1
                hide_obj
                end
        sfx 141
        flash WHITE
        obj_script SLOT_1
                action $10
                end
        pass_on SLOT_1
        dlg $0b9f                       ; Received the Magicite
                                        ; ``Bahamut.''
        give_genju BAHAMUT
        load_map $01ff, {0, 0}, DOWN, {Z_UPPER, AIRSHIP}
        set_script_mode WORLD
        end
.endproc  ; DoomGazeMagicite

        set_script_mode EVENT

; ------------------------------------------------------------------------------

; [ Tent ]

; ca/00ea
.proc Tent
        fade_in
        fade_out_song $30
        obj_script SLOT_1, ASYNC
                action 9
                wait 6
                action 39
                end
        play_song SONG_NIGHTY_NIGHT, PAUSE_CURRENT
        call _cacfbd
        wait_song
        resume_song $10
        obj_script SLOT_1
                action 9
                end
        wait_1s
        call _cac7fe, 3
        return
.endproc

; ------------------------------------------------------------------------------

; [ Warp/Warp Stone ]

; ca/0108
.proc Warp
        sfx 76
        loop 2
                obj_script SLOT_1
                        dir RIGHT
                        end
                obj_script SLOT_1
                        dir UP
                        end
                obj_script SLOT_1
                        dir LEFT
                        end
                obj_script SLOT_1
                        dir DOWN
                        end
                end_loop
        fade_out
        loop 2
                obj_script SLOT_1
                        dir RIGHT
                        end
                obj_script SLOT_1
                        dir UP
                        end
                obj_script SLOT_1
                        dir LEFT
                        end
                obj_script SLOT_1
                        dir DOWN
                        end
                end_loop
        wait_fade
        switch $01cc=0
        switch $01cd=0
        switch $02bc=0
        if_switch $02bf=1, phoenix_cave_warp
        if_switch $02be=1, kefkas_tower_warp
        call reset_turtles
        load_map $01ff, {0, 0}, DOWN, {Z_UPPER, ASYNC}
        set_script_mode WORLD
        end
        set_script_mode EVENT

; warp event when in kefka's tower
kefkas_tower_warp:
        call _cc0ff6
        return

; warp event when in phoenix cave
phoenix_cave_warp:
        call _cc1001
        return

; reset some turtles when in darill's tomb
; ca/0159
reset_turtles:
        switch $02b4=0
        switch $02b6=0
        return
.endproc  ; Warp

; ------------------------------------------------------------------------------

; [ Tent (world map) ]

; ca/015e
.proc WorldTent
        switch $01c2=0
        play_song SONG_NIGHTY_NIGHT, PAUSE_CURRENT
        obj_script SLOT_1
                action 9
                wait 6
                action 39
                end
        call _cacfbd
        wait_song
        resume_song $10
        obj_script SLOT_1
                action 9
                end
        wait_1s
        call _cac7fe, 3
        wait_30f
        load_map $01fe, {0, 0}, DOWN, Z_UPPER
        set_script_mode WORLD
        end
.endproc  ; WorldTent

        set_script_mode EVENT

; ------------------------------------------------------------------------------

