; [ Game Over ]

; cc/e566
.proc GameOver
        load_map 5, {8, 7}, LEFT, {ASYNC, NO_FADE_IN}
        fade_out_song 160
        vehicle SLOT_1, NONE
        vehicle SLOT_2, NONE
        vehicle SLOT_3, NONE
        vehicle SLOT_4, NONE
        obj_script SLOT_1
                anim_on
                layer 0
                end
        obj_script SLOT_2
                anim_on
                layer 0
                end
        obj_script SLOT_3
                anim_on
                layer 0
                end
        obj_script SLOT_4
                anim_on
                layer 0
                end
        show_obj SLOT_1
        hide_obj SLOT_2
        hide_obj SLOT_3
        hide_obj SLOT_4
        update_party
        obj_script SLOT_1
                dir LEFT
                end
        clr_overlay
        shake ALL, 0, 1
        if_switch $02ab=1, :+
        if_switch $02ac=1, :+
        if_switch $02ad=1, :+
        if_switch $02ae=1, :+
        stop_timer 0
        stop_timer 1
        stop_timer 2
        stop_timer 3
:       fade_in 4
        wait_fade
        wait_1s
        obj_script SLOT_1
                action 21
                end
        wait_30f
        obj_script SLOT_1
                action 34
                end
        wait_15f
        obj_script SLOT_1
                action 9
                end
        wait_1s
        play_song SONG_REST_IN_PEACE
        wait_1s
        dlg $06d5, {BOTTOM, TEXT_ONLY}
        fade_out_song $80
        fade_out 4
        wait_fade
        wait_90f
        hide_obj SLOT_1
        hide_obj SLOT_2
        hide_obj SLOT_3
        hide_obj SLOT_4
        sort_obj
        unlock_camera
        restore_save
        return
.endproc  ; GameOver

